import React, {useEffect, useState} from 'react';
import { Container, AppBar, Typography,Grow, Grid, ThemeProvider, useTheme} from '@mui/material';
import memories from './images/memories.jpg';
import Posts from './components/Posts/Posts.js';
import Form from './components/Form/Form.js';
import useStyles from './styles.js';
import { useDispatch } from 'react-redux';
import {getPosts} from './actions/posts';

  





function getWindowDimensions() {
    const { innerWidth: width, innerHeight: height } = window;
    return {
      width,
      height,
    };
  }
const App = ()=>{
    
    const [windowDimensions, setWindowDimensions] = useState(
        getWindowDimensions()
      );
    
      useEffect(() => {
        function handleResize() {
          setWindowDimensions(getWindowDimensions());
          console.log(windowDimensions.width);
        }
    
        window.addEventListener("resize", handleResize);
        return () => window.removeEventListener("resize", handleResize);
      }, []);



    const [currentId, setCurrentId] = useState(null);
    const classes = useStyles();
    // define the hooks
    // [dispatch] means effect only re-run when the dispatch value changes 
    const dispatch = useDispatch();
    useEffect(()=>{
        dispatch(getPosts())
    }, [currentId,dispatch])
    return (
            // <ThemeProvider theme={theme}>
            <Container maxWidth="lg">
            <AppBar className={classes.appBar} position='static' color='inherit' style={{height: '80px', width:'100%'}}>
            <img className={classes.image} src={memories} alt="memories" style={{height:'100%', width:'100%'}}/>
            <Typography className={classes.heading} variant='h2' align='center'>Memories</Typography>

            </AppBar>

            <Grow in>
                <Container>
                    <Grid container className={`${windowDimensions.width <= 600 && 'direction = column-reverse'}` }  justifyContent="space-between" alignItems="stretch" spacing={3}>
                        <Grid item  xs = {12} sm={7} >
                            <Posts setCurrentId={setCurrentId}/>
                        </Grid>

                        <Grid item  xs = {12} sm={4} >
                            <Form currentId={currentId} setCurrentId={setCurrentId} />
                       {windowDimensions.width}
                        </Grid>

                    </Grid>
                </Container>

            </Grow>
        </Container>
        // </ThemeProvider>

    );
}
export default App;
