import React, {useState, useEffect} from 'react'
import { Container, Grid, Grow,Paper } from '@mui/material';
import Posts from '../Posts/Posts'
import Form from '../Form/Form'
import { useDispatch } from 'react-redux';
import { getPosts } from '../../actions/posts';
import Pagination from '../Pagination';
function getWindowDimensions() {
    const { innerWidth: width, innerHeight: height } = window;
    return {
        width,
        height,
    };
}
const Home = () => {
    const [currentId, setCurrentId] = useState(null);
    // define the hooks
    // [dispatch] means effect only re-run when the dispatch value changes 
    const dispatch = useDispatch();
    useEffect(()=>{
        dispatch(getPosts())
    }, [currentId,dispatch])
    const [windowDimensions, setWindowDimensions] = useState(
        getWindowDimensions()
    );
    useEffect(() => {
        function handleResize() {
            setWindowDimensions(getWindowDimensions());
            console.log(windowDimensions.width);
        }

        window.addEventListener("resize", handleResize);
        return () => window.removeEventListener("resize", handleResize);
    }, []);

    return (
        <Grow in>
            <Container>
                <Grid container direction={windowDimensions.width <= 600 ? 'column-reverse' : 'row'}
                    justifyContent="space-between" alignItems="stretch" spacing={3}>
                    <Grid item xs={12} sm={7} >
                        <Posts setCurrentId={setCurrentId} />
                    </Grid>

                    <Grid item xs={12} sm={4} >
                        <Form currentId={currentId} setCurrentId={setCurrentId} />
                        {windowDimensions.width}
                        <Paper elevation={6}>
                            <Pagination/>
                        </Paper>
                    </Grid>

                </Grid>
            </Container>

        </Grow>
    )
}

export default Home;
