                        onAddChip={handleAdd}
                        onDeleteChip={handleDelete}
                        />
                       </AppBar>
                       
                        <Form currentId={currentId} setCurrentId={setCurrentId} />
                        
                        <Paper elevation={6}>
                           
                            <Pagination/>
                        
                        </Paper>
                    </Grid>

                </Grid>
            </Container>

        </Grow>
    )
}

export default Home;
